mapper.select:
select id, money, year, month, employee_id, payout_id
from salary
where id = #{id}

select s.id, s.money, s.year, s.month, s.employee_id e_id, s.payout_id sdi_id, e.name e_name, sdi.title sdi_title from salary s left join employee e on s.employee_id = e.id left join system_dictionary_item sdi on s.payout_id = sdi.id
and e.name like concat('%',#{keyword},'%')
and s.payout_id =#{payout.id}
and s.money >=#{minMoney}
and s.money <=#{maxMoney}
order by s.year desc ,s.month desc ,s.money desc
